import React, { useEffect, useState } from 'react';
import { ShoppingItem } from '../components/ShoppingItem';
import { fetchShoppingLists } from '../utils/api';

const ShoppingList = () => {
  const [shoppingLists, setShoppingLists] = useState([]);

  useEffect(() => {
    const getShoppingLists = async () => {
      const lists = await fetchShoppingLists();
      setShoppingLists(lists);
    };

    getShoppingLists();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-gray-800 mb-4">Your Shopping Lists</h1>
      {shoppingLists.length === 0 ? (
        <p className="text-gray-500">No shopping lists found.</p>
      ) : (
        <div className="grid grid-cols-1 gap-4">
          {shoppingLists.map((list) => (
            <ShoppingItem key={list.id} name={list.name} quantity={list.items.length} />
          ))}
        </div>
      )}
    </div>
  );
};

export default ShoppingList;
