import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api'; // Update with your backend URL

// Function to get all grocery items
export const getGroceryItems = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/grocery-items`);
    return response.data;
  } catch (error) {
    console.error('Error fetching grocery items:', error);
    throw error;
  }
};

// Function to add a new grocery item
export const addGroceryItem = async (item) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/grocery-items`, item);
    return response.data;
  } catch (error) {
    console.error('Error adding grocery item:', error);
    throw error;
  }
};

// Function to update an existing grocery item
export const updateGroceryItem = async (id, item) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/grocery-items/${id}`, item);
    return response.data;
  } catch (error) {
    console.error('Error updating grocery item:', error);
    throw error;
  }
};

// Function to delete a grocery item
export const deleteGroceryItem = async (id) => {
  try {
    await axios.delete(`${API_BASE_URL}/grocery-items/${id}`);
  } catch (error) {
    console.error('Error deleting grocery item:', error);
    throw error;
  }
};
